export default function IraqMap() {
  const [hoveredGovernorate, setHoveredGovernorate] = useState<string | null>(null);

  const totalOrders = iraqi_governorates.reduce((sum, gov) => sum + gov.orders, 0);

  const getGovernorateColor = (governorateId: string, orders: number) => {
    const isHovered = hoveredGovernorate === governorateId;
    
    // تدرج الألوان حسب عدد الطلبات
    if (orders >= 100) return isHovered ? "#7c3aed" : "#8b5cf6"; // بنفسجي غامق
    if (orders >= 50) return isHovered ? "#3b82f6" : "#60a5fa";   // أزرق
    if (orders >= 25) return isHovered ? "#10b981" : "#34d399";   // أخضر
    return isHovered ? "#f59e0b" : "#fbbf24"; // أصفر
  };

  const getOrdersPercentage = (orders: number) => {
    return ((orders / totalOrders) * 100).toFixed(1);
  };

  return (
    <Card className="theme-border bg-theme-primary-lighter">
      <CardHeader className="bg-theme-primary-light border-b border-theme-primary pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-bold text-theme-primary flex items-center">
            <MapPin className="w-4 h-4 mr-2" />
            توزيع الطلبات حسب المحافظات
          </CardTitle>
          <Badge className="bg-theme-gradient text-white text-xs px-2 py-1">
            <Users className="w-3 h-3 mr-1" />
            {totalOrders} طلب
          </Badge>
        </div>
      </CardHeader>
      
      <CardContent className="p-6">
        <div className="flex flex-col lg:flex-row gap-6">
          {/* خريطة العراق */}
          <div className="flex-1">
            <div className="relative bg-gradient-to-br from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-900 rounded-xl p-4 border border-theme-primary">
              <svg
                viewBox="0 0 800 600"
                className="w-full h-80"
                style={{ maxWidth: '500px', margin: '0 auto', display: 'block' }}
                preserveAspectRatio="xMidYMid meet"
              >
                {iraqi_governorates.map((governorate) => (
                  <g key={governorate.id}>
                    <path
                      d={governorate.coordinates}
                      fill={getGovernorateColor(governorate.id, governorate.orders)}
                      stroke="#ffffff"
                      strokeWidth="2"
                      className="transition-all duration-200 cursor-pointer hover:stroke-theme-primary hover:stroke-3"
                      onMouseEnter={() => setHoveredGovernorate(governorate.id)}
                      onMouseLeave={() => setHoveredGovernorate(null)}
                    />
                  </g>
                ))}
                
                {/* أرقام الطلبات على الخريطة */}
                {iraqi_governorates.map((governorate) => {
                  // حساب نقطة المركز التقريبي لكل محافظة بطريقة مبسطة
                  const coords = governorate.coordinates.match(/M\s*(\d+),(\d+)/);
                  if (!coords) return null;
                  
                  const centerX = parseInt(coords[1]) + 15; // إضافة offset
                  const centerY = parseInt(coords[2]) + 8; // إضافة offset
                  
                  return (
                    <text
                      key={`label-${governorate.id}`}
                      x={centerX}
                      y={centerY}
                      textAnchor="middle"
                      dominantBaseline="central"
                      className="fill-white text-xs font-bold pointer-events-none drop-shadow-lg"
                      style={{ fontSize: '12px', textShadow: '1px 1px 2px rgba(0,0,0,0.8)' }}
                    >
                      {governorate.orders}
                    </text>
                  );
                })}
                
                {/* عرض معلومات المحافظة عند التمرير */}
                {hoveredGovernorate && (
                  <foreignObject x="20" y="20" width="200" height="80">
                    <div className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-3 border border-theme-primary">
                      <div className="text-sm font-bold text-theme-primary">
                        {iraqi_governorates.find(g => g.id === hoveredGovernorate)?.name}
                      </div>
                      <div className="text-xs text-gray-600 dark:text-gray-400 mt-1">
                        الطلبات: {iraqi_governorates.find(g => g.id === hoveredGovernorate)?.orders}
                      </div>
                      <div className="text-xs text-gray-500 dark:text-gray-500">
                        النسبة: {getOrdersPercentage(iraqi_governorates.find(g => g.id === hoveredGovernorate)?.orders || 0)}%
                      </div>
                    </div>
                  </foreignObject>
                )}
              </svg>
            </div>
          </div>

          {/* قائمة المحافظات */}
          <div className="lg:w-80">
            <div className="bg-white dark:bg-gray-800 rounded-xl border border-theme-primary p-4">
              <h3 className="text-sm font-bold text-theme-primary mb-3 border-b border-theme-primary pb-2">
                ترتيب المحافظات حسب عدد الطلبات
              </h3>
              <div className="space-y-2 max-h-80 overflow-y-auto">
                {iraqi_governorates
                  .sort((a, b) => b.orders - a.orders)
                  .map((governorate, index) => (
                    <div
                      key={governorate.id}
                      className={`flex items-center justify-between p-2 rounded-lg transition-all duration-200 cursor-pointer ${
                        hoveredGovernorate === governorate.id
                          ? 'bg-theme-primary-light border border-theme-primary'
                          : 'hover:bg-gray-50 dark:hover:bg-gray-700'
                      }`}
                      onMouseEnter={() => setHoveredGovernorate(governorate.id)}
                      onMouseLeave={() => setHoveredGovernorate(null)}
                    >
                      <div className="flex items-center space-x-3">
                        <div className={`w-6 h-6 rounded-full flex items-center justify-center text-xs font-bold ${
                          index < 3 ? 'bg-theme-gradient text-white' : 'bg-gray-200 dark:bg-gray-600 text-gray-600 dark:text-gray-300'
                        }`}>
                          {index + 1}
                        </div>
                        <span className="text-sm font-medium text-gray-800 dark:text-gray-200">
                          {governorate.name}
                        </span>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Badge 
                          className="text-xs"
                          style={{ 
                            backgroundColor: getGovernorateColor(governorate.id, governorate.orders),
                            color: 'white'
                          }}
                        >
                          {governorate.orders}
                        </Badge>
                        <span className="text-xs text-gray-500">
                          {getOrdersPercentage(governorate.orders)}%
                        </span>
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </div>

        {/* مفتاح الألوان */}
        <div className="mt-6 pt-4 border-t border-theme-primary">
          <h4 className="text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">مفتاح الألوان:</h4>
          <div className="flex flex-wrap gap-4">
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-purple-500 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">100+ طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-blue-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">50-99 طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-green-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">25-49 طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-yellow-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">أقل من 25 طلب</span>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}