export default function IraqMap() {
  const [hoveredGovernorate, setHoveredGovernorate] = useState<string | null>(null);
  const [transform, setTransform] = useState({ x: 0, y: 0, scale: 1 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // جلب بيانات الجلسة
  const { data: session } = useQuery<PlatformSession>({
    queryKey: ['/api/platform-session'],
  });

  // جلب إحصائيات المحافظات للمنصة الحالية
  const { data: governorateStats, error: statsError, isLoading: statsLoading } = useQuery({
    queryKey: ['/api/platform/governorate-stats'],
    enabled: !!session?.platformId,
    queryFn: async () => {
      console.log('🔍 Fetching governorate stats for platform:', session?.platformId);
      const response = await fetch('/api/platform/governorate-stats', {
        credentials: 'include'
      });
      
      if (!response.ok) {
        console.error('❌ API response not ok:', response.status, response.statusText);
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('✅ Governorate stats received:', data);
      return data;
    }
  });

  // تشخيص البيانات في حالة التطوير فقط
  if (process.env.NODE_ENV === 'development') {
    console.log('🔍 Session:', session);
    console.log('🔍 GovernorateStats:', governorateStats);
    console.log('🔍 Stats Error:', statsError);
    console.log('🔍 Stats Loading:', statsLoading);
  }

  // خريطة ربط بين الأسماء العربية والإنجليزية للمحافظات
  const governorateMapping: Record<string, string> = {
    'الأنبار': 'anbar',
    'نينوى': 'nineveh', 
    'دهوك': 'duhok',
    'أربيل': 'erbil',
    'صلاح الدين': 'salahaldin',
    'حلبجة': 'halabja',
    'السليمانية': 'sulaymaniyah',
    'كركوك': 'kirkuk',
    'ديالى': 'diyala',
    'بغداد': 'baghdad',
    'بابل': 'babylon',
    'كربلاء': 'karbala',
    'النجف': 'najaf',
    'القادسية': 'qadisiyyah',
    'المثنى': 'muthanna',
    'ذي قار': 'dhi_qar',
    'ميسان': 'maysan',
    'البصرة': 'basra',
    'واسط': 'wasit'
  };

  // دمج البيانات الحقيقية مع إحداثيات المحافظات الثابتة
  const governorateData: GovernorateData[] = iraqi_governorates.map((gov) => {
    // البحث عن البيانات الحقيقية للمحافظة بالـ ID المطابق
    const realData = (governorateStats as any)?.find((g: any) => g.id === gov.id);
    
    // اطبع معلومات المطابقة في حالة التطوير فقط
    if (process.env.NODE_ENV === 'development') {
      if (realData) {
        console.log(`✅ تم العثور على بيانات لـ ${gov.name}: ${realData.orders} طلب`);
      } else {
        console.log(`❌ لم يتم العثور على بيانات لـ ${gov.name} (ID: ${gov.id})`);
      }
    }
    
    return {
      id: gov.id,
      name: gov.name,
      orders: realData?.orders || 0,
      revenue: realData?.revenue || 0,
      coordinates: gov.coordinates
    };
  });

  const totalOrders = governorateData.reduce((sum, gov) => sum + gov.orders, 0);
  
  // تشخيص في حالة التطوير فقط
  if (process.env.NODE_ENV === 'development') {
    console.log('🔍 Total Orders:', totalOrders);
    console.log('🔍 Governorate Data Length:', governorateData.length);
    console.log('🔍 Sample Governorate Data:', governorateData.slice(0, 3));
  }

  const getGovernorateColor = (governorateId: string, orders: number) => {
    const isHovered = hoveredGovernorate === governorateId;
    
    // تدرج الألوان حسب عدد الطلبات
    if (orders >= 100) return isHovered ? "#7c3aed" : "#8b5cf6"; // بنفسجي غامق
    if (orders >= 50) return isHovered ? "#3b82f6" : "#60a5fa";   // أزرق
    if (orders >= 25) return isHovered ? "#10b981" : "#34d399";   // أخضر
    return isHovered ? "#f59e0b" : "#fbbf24"; // أصفر
  };

  const getOrdersPercentage = (orders: number) => {
    if (totalOrders === 0) return "0.0";
    return ((orders / totalOrders) * 100).toFixed(1);
  };

  // وظائف التكبير والتصغير والسحب
  const handleWheel = useCallback((e: React.WheelEvent) => {
    e.preventDefault();
    e.stopPropagation();
    
    const delta = e.deltaY > 0 ? 0.9 : 1.1;
    const newScale = Math.max(0.3, Math.min(8, transform.scale * delta));
    
    if (svgRef.current) {
      const rect = svgRef.current.getBoundingClientRect();
      
      // حساب موضع الماوس بالنسبة للـ SVG
      const mouseX = e.clientX - rect.left;
      const mouseY = e.clientY - rect.top;
      
      // تحويل موضع الماوس إلى إحداثيات SVG
      const svgX = (mouseX - transform.x) / transform.scale;
      const svgY = (mouseY - transform.y) / transform.scale;
      
      // حساب الموضع الجديد بحيث يبقى موضع الماوس ثابتاً
      const newX = mouseX - svgX * newScale;
      const newY = mouseY - svgY * newScale;
      
      setTransform({ x: newX, y: newY, scale: newScale });
    }
  }, [transform]);

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    if (e.button === 0) { // الزر الأيسر فقط
      setIsDragging(true);
      setDragStart({ x: e.clientX - transform.x, y: e.clientY - transform.y });
      e.preventDefault();
    }
  }, [transform]);

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (isDragging) {
      const newX = e.clientX - dragStart.x;
      const newY = e.clientY - dragStart.y;
      setTransform(prev => ({ ...prev, x: newX, y: newY }));
    }
  }, [isDragging, dragStart]);

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
  }, []);

  // إضافة مستمع أحداث مباشر لمنع تحرك الصفحة
  useEffect(() => {
    const handleWheelEvent = (e: WheelEvent) => {
      e.preventDefault();
      e.stopPropagation();
      e.stopImmediatePropagation();
      
      const delta = e.deltaY > 0 ? 0.9 : 1.1;
      const newScale = Math.max(0.3, Math.min(8, transform.scale * delta));
      
      // التكبير من المركز مع الحفاظ على الموضع المركزي
      setTransform(prev => ({
        x: prev.x * (newScale / prev.scale),
        y: prev.y * (newScale / prev.scale),
        scale: newScale
      }));
      
      return false;
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('wheel', handleWheelEvent, { passive: false });
      return () => {
        container.removeEventListener('wheel', handleWheelEvent);
      };
    }
  }, [transform]);

  const resetZoom = useCallback(() => {
    setTransform({ x: 0, y: 0, scale: 1 });
  }, []);

  const zoomIn = useCallback(() => {
    const newScale = Math.min(8, transform.scale * 1.2);
    if (svgRef.current) {
      const rect = svgRef.current.getBoundingClientRect();
      const centerX = rect.width / 2;
      const centerY = rect.height / 2;
      
      const svgX = (centerX - transform.x) / transform.scale;
      const svgY = (centerY - transform.y) / transform.scale;
      
      const newX = centerX - svgX * newScale;
      const newY = centerY - svgY * newScale;
      
      setTransform({ x: newX, y: newY, scale: newScale });
    }
  }, [transform]);

  const zoomOut = useCallback(() => {
    const newScale = Math.max(0.3, transform.scale * 0.8);
    if (svgRef.current) {
      const rect = svgRef.current.getBoundingClientRect();
      const centerX = rect.width / 2;
      const centerY = rect.height / 2;
      
      const svgX = (centerX - transform.x) / transform.scale;
      const svgY = (centerY - transform.y) / transform.scale;
      
      const newX = centerX - svgX * newScale;
      const newY = centerY - svgY * newScale;
      
      setTransform({ x: newX, y: newY, scale: newScale });
    }
  }, [transform]);

  return (
    <Card className="theme-border bg-theme-primary-lighter w-full lg:w-2/3">
      <CardHeader className="bg-theme-primary-light border-b border-theme-primary pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="text-lg font-bold text-theme-primary flex items-center">
            <MapPin className="w-4 h-4 mr-2" />
            توزيع الطلبات حسب المحافظات
          </CardTitle>
          <Badge className="bg-theme-gradient text-white text-xs px-2 py-1">
            {totalOrders} طلب
          </Badge>
        </div>
      </CardHeader>
      <CardContent className="p-4">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* خريطة العراق */}
          <div className="flex-1 lg:w-2/3">
            <div className="relative bg-gradient-to-br from-blue-50 to-purple-50 dark:from-gray-800 dark:to-gray-900 rounded-xl p-4 border border-theme-primary overflow-hidden">
              {/* أزرار التحكم */}
              <div className="absolute top-6 right-6 z-10 flex flex-col gap-2">
                <button
                  onClick={zoomIn}
                  className="w-8 h-8 bg-white dark:bg-gray-700 rounded-lg shadow-lg border border-gray-200 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-600 transition-colors"
                  title="تكبير"
                >
                  <span className="text-lg font-bold text-gray-700 dark:text-gray-300">+</span>
                </button>
                <button
                  onClick={zoomOut}
                  className="w-8 h-8 bg-white dark:bg-gray-700 rounded-lg shadow-lg border border-gray-200 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-600 transition-colors"
                  title="تصغير"
                >
                  <span className="text-lg font-bold text-gray-700 dark:text-gray-300">-</span>
                </button>
                <button
                  onClick={resetZoom}
                  className="w-8 h-8 bg-white dark:bg-gray-700 rounded-lg shadow-lg border border-gray-200 dark:border-gray-600 flex items-center justify-center hover:bg-gray-50 dark:hover:bg-gray-600 transition-colors"
                  title="إعادة تعيين"
                >
                  <span className="text-xs font-bold text-gray-700 dark:text-gray-300">⌂</span>
                </button>
              </div>
              
              {/* مؤشر التكبير */}
              <div className="absolute bottom-6 right-6 z-10 bg-white dark:bg-gray-700 rounded-lg shadow-lg border border-gray-200 dark:border-gray-600 px-2 py-1">
                <span className="text-xs text-gray-600 dark:text-gray-400">
                  {Math.round(transform.scale * 100)}%
                </span>
              </div>
              
              <div 
                ref={containerRef}
                className="w-full h-[400px] overflow-hidden rounded-lg"
              >
                <svg
                  ref={svgRef}
                  viewBox="0 0 1000 1000"
                  className="w-full h-full cursor-grab active:cursor-grabbing"
                  style={{ 
                    transform: `translate(${transform.x}px, ${transform.y}px) scale(${transform.scale})`,
                    transformOrigin: 'center center',
                    transition: isDragging ? 'none' : 'transform 0.1s ease-out'
                  }}
                  preserveAspectRatio="xMidYMid meet"
                  onMouseDown={handleMouseDown}
                  onMouseMove={handleMouseMove}
                  onMouseUp={handleMouseUp}
                  onMouseLeave={handleMouseUp}
                >
                {governorateData.map((governorate) => (
                  <g key={governorate.id}>
                    <path
                      d={governorate.coordinates}
                      fill={getGovernorateColor(governorate.id, governorate.orders)}
                      stroke="#ffffff"
                      strokeWidth="2"
                      className="transition-all duration-200 cursor-pointer hover:stroke-theme-primary hover:stroke-3"
                      onMouseEnter={() => setHoveredGovernorate(governorate.id)}
                      onMouseLeave={() => setHoveredGovernorate(null)}
                    />
                  </g>
                ))}
                
                {/* أرقام الطلبات على الخريطة */}
                {governorateData.map((governorate) => {
                  // حساب نقطة المركز التقريبي لكل محافظة بطريقة مبسطة
                  const coords = governorate.coordinates.match(/M\s*(\d+(?:\.\d+)?)\s+(\d+(?:\.\d+)?)/);
                  if (!coords) return null;
                  
                  const centerX = parseFloat(coords[1]) + 15; // إضافة offset
                  const centerY = parseFloat(coords[2]) + 8; // إضافة offset
                  
                  return (
                    <text
                      key={`label-${governorate.id}`}
                      x={centerX}
                      y={centerY}
                      textAnchor="middle"
                      dominantBaseline="central"
                      className="fill-white text-xs font-bold pointer-events-none drop-shadow-lg"
                      style={{ fontSize: '12px', textShadow: '1px 1px 2px rgba(0,0,0,0.8)' }}
                    >
                      {governorate.orders > 0 ? governorate.orders : ''}
                    </text>
                  );
                })}
                
                {/* عرض معلومات المحافظة عند التمرير */}
                {hoveredGovernorate && (() => {
                  const governorate = governorateData.find(g => g.id === hoveredGovernorate);
                  if (!governorate) return null;
                  
                  // حساب موضع المحافظة من الإحداثيات
                  const coords = governorate.coordinates.match(/M\s*(\d+(?:\.\d+)?)\s+(\d+(?:\.\d+)?)/);
                  if (!coords) return null;
                  
                  const govX = parseFloat(coords[1]);
                  const govY = parseFloat(coords[2]);
                  
                  // تحديد موضع البطاقة بجانب المحافظة
                  const tooltipX = govX + 50; // إزاحة يمين
                  const tooltipY = govY - 20; // إزاحة أعلى قليلاً
                  
                  return (
                    <foreignObject x={tooltipX} y={tooltipY} width="140" height="140">
                      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-2xl p-3 border-2 border-theme-primary transform transition-all duration-200 scale-105 w-full h-full flex flex-col justify-center">
                        <div className="text-base font-bold text-theme-primary mb-2">
                          {governorate.name}
                        </div>
                        <div className="text-sm text-gray-700 dark:text-gray-300 mb-1">
                          عدد الطلبات: <span className="font-bold text-theme-primary">{governorate.orders}</span>
                        </div>
                        <div className="text-sm text-gray-600 dark:text-gray-400">
                          النسبة: <span className="font-bold">{getOrdersPercentage(governorate.orders)}%</span>
                        </div>
                        <div className="mt-2 h-1 bg-gray-200 dark:bg-gray-600 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-theme-primary rounded-full transition-all duration-500"
                            style={{ width: `${getOrdersPercentage(governorate.orders)}%` }}
                          ></div>
                        </div>
                      </div>
                    </foreignObject>
                  );
                })()}
              </svg>
              </div>
            </div>
          </div>

          {/* قائمة المحافظات */}
          <div className="lg:w-1/3">
            <div className="bg-white dark:bg-gray-800 rounded-xl border border-theme-primary p-3">
              <h3 className="text-xs font-bold text-theme-primary mb-2 border-b border-theme-primary pb-2">
                ترتيب المحافظات حسب عدد الطلبات
              </h3>
              <div className="space-y-1.5 max-h-80 overflow-y-auto">
                {governorateData
                  .sort((a, b) => b.orders - a.orders)
                  .map((governorate, index) => (
                    <div
                      key={governorate.id}
                      className={`flex items-center justify-between p-2 rounded-lg transition-all duration-200 cursor-pointer ${
                        hoveredGovernorate === governorate.id
                          ? 'bg-theme-primary-light border border-theme-primary'
                          : 'hover:bg-gray-50 dark:hover:bg-gray-700'
                      }`}
                      onMouseEnter={() => setHoveredGovernorate(governorate.id)}
                      onMouseLeave={() => setHoveredGovernorate(null)}
                    >
                      <div className="text-right">
                        <div className="text-xs font-medium text-theme-primary">
                          {governorate.name}
                        </div>
                        <div className="text-xs text-gray-500 dark:text-gray-400">
                          #{index + 1}
                        </div>
                      </div>
                      <div className="flex items-center gap-1">
                        <Badge 
                          variant="outline" 
                          className="bg-theme-primary-light text-theme-primary border-theme-primary text-xs px-1.5 py-0.5"
                        >
                          {governorate.orders}
                        </Badge>
                        <div className="w-1.5 h-1.5 rounded-full bg-theme-primary animate-pulse"></div>
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </div>

        {/* مفتاح الألوان */}
        <div className="mt-4 pt-3 border-t border-theme-primary">
          <h4 className="text-xs font-medium text-gray-700 dark:text-gray-300 mb-2">مفتاح الألوان:</h4>
          <div className="flex flex-wrap gap-3">
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-purple-500 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">100+ طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-blue-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">50-99 طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-green-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">25-49 طلب</span>
            </div>
            <div className="flex items-center space-x-2">
              <div className="w-4 h-4 bg-yellow-400 rounded"></div>
              <span className="text-xs text-gray-600 dark:text-gray-400">أقل من 25 طلب</span>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}